function generateGameName(length = 6) {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    return Array.from({ length }, () => chars[Math.floor(Math.random() * chars.length)]).join('');
}

function generateMessage(tries) {
    return tries < 6 ? `You have ${6 - tries} left` : `You have lost`;
}

function generateMessageForRiddler(tries) {
    return tries < 6 ? `Opponent has ${6 - tries} left` : `You have won`;
}

function generateState(puzzle, guess, currentState) {
    const strippedState = currentState.replace(/-/g, '');
    return puzzle.split('').map((char, i) => (char === guess ? (i === 0 ? guess : `-${guess}`) : `-${strippedState[i] || ''}`)).join('');
}

module.exports = { generateGameName, generateMessage, generateMessageForRiddler, generateState };
